import React from 'react'

import styled from 'styled-components'

import useTodos from './useTodos'

import TodoList from './components/TodoList'
import AddTodo from './components/AddTodo'

function App() {
  const { list, createTodo, toggleComplete } = useTodos()
  const [pendingFilter, setpendingFilter] = React.useState()
  const [completedFilter, setcompletedFilter] = React.useState()

  return (
    <Wrapper>
      <TodosWrapper>
        <AddTodo onAddTodo={createTodo} />
        <TodoList items={list} toggleComplete={toggleComplete} />
        {console.log('pending' , )}
        {list.filter(ts => setpendingFilter(ts.completed == false))}
        {console.log('completed' ,)}
        {list.filter(ts => completedFilter.push(ts.completed == true))}
        <div className="row">
          <div className="col-sm">
            <h3>Filter</h3>
            <ul>
              <h4>Complated</h4>
              {/* {completedFilter.map((item) => (
                <li>{item.text}</li>
              ))} */}
            </ul>
            <ul>
              <h4>Pending</h4>
              {/* {pendingFilter.map((item) => (
                <li>{item.text}</li>
              ))} */}
            </ul>
          </div>
        </div>
      </TodosWrapper>
    </Wrapper>
  )
}

const Wrapper = styled.div`
  background-color: #282c34;
  min-height: 100vh;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  font-size: 24px;
  color: white;
`

const TodosWrapper = styled.div`
  max-width: 500px;
  display: flex;
  flex-direction: column;
`

export default App
