import React from 'react'
import "./index.css"
import styled from 'styled-components'

import useTodos from './useTodos'

import TodoList from './components/TodoList'
import AddTodo from './components/AddTodo'

function App() {
  const { list, createTodo, toggleComplete } = useTodos()
  const [pendingFilter, setpendingFilter] = React.useState([])
  const [completedFilter, setcompletedFilter] = React.useState([])

  const pending = list.filter(ts => ts.completed == false)
  const completed = list.filter(ts => ts.completed == true)

  return (
    <Wrapper>
       <div className="todoc">
            <h3>Filter</h3>
            <ul>
              <h4>Completed</h4>
              {completed.map((item) => (
                <li>{item.text}</li>
              ))}
            </ul>
            <ul>
              <h4>Pending</h4>
              {pending.map((item) => (
                <li>{item.text}</li>
              ))}
            </ul>
        </div>
      <TodosWrapper>
        <AddTodo onAddTodo={createTodo} />
        <TodoList items={list} toggleComplete={toggleComplete} />
        {console.log('pending' , list.filter(ts => ts.completed == false))}
        {console.log('completed' , list.filter(ts => ts.completed == true))}
       
      </TodosWrapper>
    </Wrapper>
  )
}

const Wrapper = styled.div`
  background-color: white;
  align-items: right;
  justify-content: right;
  font-size: 24px;
  color: grey;
`

const TodosWrapper = styled.div`
  min-width: 500;
  margin-bottom:500px;
`

export default App
